create table notifications (id bigint generated by default as identity, created_at timestamp, text varchar(255), updated_at timestamp, task_id bigint, user_id bigint, primary key (id));
create table projects (id bigint generated by default as identity, created_at timestamp, creator_id bigint, description varchar(255), name varchar(255), updated_at timestamp, primary key (id));
create table tasks (id bigint generated by default as identity, archived boolean, created_at timestamp, deadline_time timestamp, description varchar(255), name varchar(255), updated_at timestamp, primary key (id));
create table tasksauthorities (id bigint generated by default as identity, name varchar(255), primary key (id));
create table taskstates (id bigint generated by default as identity, codename varchar(255), name varchar(255), primary key (id));
create table users (id bigint generated by default as identity, created_at timestamp, email varchar(255), name varchar(255), password varchar(255), surname varchar(255), updated_at timestamp, username varchar(255), primary key (id));
create table users_projects (user_id bigint not null, project_id bigint not null);
create table users_tasks (id bigint generated by default as identity, task_id bigint, user_id bigint, auth_id bigint, primary key (id));
alter table notifications add constraint FK2ktjq1slw0ldkuy5rx8fbte2p foreign key (task_id) references tasks;
alter table notifications add constraint FK9y21adhxn0ayjhfocscqox7bh foreign key (user_id) references users;
alter table users_projects add constraint FKkoh0xgfur21fdanq862b3qx7n foreign key (project_id) references projects;
alter table users_projects add constraint FKen924y69h6d6chaojjgqfaow8 foreign key (user_id) references users;
alter table users_tasks add constraint FK7todmyl52eiddpi6hc2nfgvbs foreign key (task_id) references tasks;
alter table users_tasks add constraint FK6frwjo48hefay0rg31q970r8t foreign key (user_id) references users;
alter table users_tasks add constraint FKhbao4fgxe01qnjarg9i2ylgvv foreign key (auth_id) references tasksauthorities;